import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { MoviesService } from 'src/app/services/movies-service';

@Component({
  selector: 'app-movie-details',
  templateUrl: './movie-details.component.html',
  styleUrls: ['./movie-details.component.less'],
})
export class MovieDetailsComponent implements OnInit {
  movieId: any;
  movie: any = [];

  constructor(
    private route: ActivatedRoute,
    private moviesService: MoviesService
  ) {}

  ngOnInit(): void {
    this.movieId = this.route.snapshot.paramMap.get('id');

    this.moviesService.getMovies().subscribe(
      (response) => {
        this.movie = response[parseInt(this.movieId)];
      },
      (error) => {
        console.error('Erro ao obter o array de dados falsos:', error);
      }
    );
  }

  getImagePath(movieTitle: string) {
    let urlSplited = movieTitle.split(' ');
    return `/assets/images/${urlSplited[0]}.png`;
  }
}
<body>
  <div class="content row">
    <div class="col-6 image">
      <img [src]="getImagePath(movie.title)" width="400" height="600" />
    </div>
    <div class="col-6 description">
      <h2>Title: {{ movie.title }}</h2>
      <p>Duration: {{ movie.duration }}</p>
      <p>Genre: {{ movie.genre }}</p>
    </div>
  </div>
</body>
