import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MoviesService } from 'src/app/services/movies-service';

@Component({
  selector: 'app-dash',
  templateUrl: './dash.component.html',
  styleUrls: ['./dash.component.less'],
})
export class DashComponent implements OnInit {
  moviesListForm!: FormGroup;
  moviesList!: any[];

  constructor(private fb: FormBuilder, private moviesService: MoviesService) {}

  ngOnInit(): void {
    this.createForm();

    this.moviesService.getMovies().subscribe(
      (response) => {
        this.moviesList = response;
        // Faça qualquer manipulação adicional aqui, se necessário
      },
      (error) => {
        console.error('Erro ao obter o array de dados falsos:', error);
      }
    );
  }

  createForm(): void {
    this.moviesListForm = this.fb.group({
      nome: ['', Validators.required],
      empresa: ['', [Validators.required]],
      formato: ['', [Validators.required]],
      tipo: [''],
      status: ['', [Validators.required]],
      tipoVeiculo: ['', [Validators.required]],
      dataInicial: ['', [Validators.required]],
      dataFinal: ['', [Validators.required]],
    });
  }

  getMoviesData() {}
}
<body>
  <h1>Quick Suit Training Films</h1>

  <div class="movies" *ngFor="let movie of moviesList">
    <h2>Title: {{ movie.title }}</h2>
    <p>Description: {{ movie.description }}</p>
    <p>Duration: {{ movie.duration }}</p>
    <p>Genre: {{ movie.genre }}</p>
    <p>Rating: {{ movie.reting }}</p>
    <p>Released date: {{ movie.releasedDate }}</p>
    <iframe
      width="560"
      height="315"
      src="{{ movie.trailerLink }}"
      frameborder="0"
      allowfullscreen
    ></iframe>
  </div>
</body>
