import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MoviesService } from 'src/app/services/movies-service';
import { DomSanitizer, SafeResourceUrl } from '@angular/platform-browser';

@Component({
  selector: 'app-dash',
  templateUrl: './dash.component.html',
  styleUrls: ['./dash.component.less'],
})
export class DashComponent implements OnInit {
  moviesListForm!: FormGroup;
  moviesList: any = [];

  constructor(
    private fb: FormBuilder,
    private moviesService: MoviesService,
    private sanitizer: DomSanitizer
  ) {}

  ngOnInit(): void {
    this.moviesService.getMovies().subscribe(
      (response) => {
        response.forEach((movie: any) => {
          movie['imagePath'] = this.getImagePath(movie.title);
          this.moviesList.push(movie);
        });
      },
      (error) => {
        console.error('Erro ao obter o array de dados falsos:', error);
      }
    );
  }

  // createForm(): void {
  //   this.moviesListForm = this.fb.group({
  //     nome: ['', Validators.required],
  //     empresa: ['', [Validators.required]],
  //     formato: ['', [Validators.required]],
  //     tipo: [''],
  //     status: ['', [Validators.required]],
  //     tipoVeiculo: ['', [Validators.required]],
  //     dataInicial: ['', [Validators.required]],
  //     dataFinal: ['', [Validators.required]],
  //   });
  // }

  getImagePath(movieUrl: string) {
    let urlSplited = movieUrl.split(' ');
    return `/assets/images/${urlSplited[0]}.png`;
  }

  sortBy(sortParam: string) {
    if (sortParam === 'title') {
      this.moviesList.sort((a: any, b: any) => {
        const movieA = a['title'].toLowerCase();
        const movieB = b['title'].toLowerCase();
        return movieA.localeCompare(movieB);
      });
    }
    if (sortParam === 'release') {
      this.moviesList.sort((a: any, b: any) => {
        const movieA = a['releasedDate'].toLowerCase();
        const movieB = b['releasedDate'].toLowerCase();
        return movieA.localeCompare(movieB);
      });
    }
  }
}
<header>
  <h1>Quick Suit Training Films</h1>
</header>

<div class="movie">
  Sort by:
  <button (click)="sortBy('title')">Title</button>
  <button (click)="sortBy('release')">Release date</button>
</div>

<div class="movie" *ngFor="let movie of moviesList">
  <img [src]="movie.imagePath" width="90" />
  <div class="movie-data">
    <h2>Title: {{ movie.title }}</h2>
    <p>Duration: {{ movie.duration }}</p>
    <p>Genre: {{ movie.genre }}</p>
    <!-- <p>Description: {{ movie.description }}</p>
        <p>Rating: {{ movie.reting }}</p>
        <p>Released date: {{ movie.releasedDate }}</p> -->
  </div>
  <!-- {{ movie | json }} -->
</div>
